collections/list-box-rearrange-demo.raw:
'use client'

import { useDragAndDrop } from 'react-aria-components'
import { useListData } from 'react-stately'
import { ListBox, ListBoxItem } from 'ui'

export default function ListBoxRearrangeDemo() {
  const list = useListData({
    initialItems: [
      { id: '1', name: 'Nirvana' },
      { id: '2', name: 'Radiohead' },
      { id: '3', name: 'Foo Fighters' },
      { id: '4', name: 'Arctic Monkeys' },
      { id: '5', name: 'The Strokes' }
    ]
  })

  const { dragAndDropHooks } = useDragAndDrop({
    getItems: (keys) => [...keys].map((key) => ({ 'text/plain': list.getItem(key).name })),
    onReorder(e) {
      if (e.target.dropPosition === 'before') {
        list.moveBefore(e.target.key, e.keys)
      } else if (e.target.dropPosition === 'after') {
        list.moveAfter(e.target.key, e.keys)
      }
    }
  })

  return (
    <ListBox
      items={list.items}
      aria-label="Bands"
      selectionMode="multiple"
      dragAndDropHooks={dragAndDropHooks}
    >
      {(item) => <ListBoxItem key={item.id}>{item.name}</ListBoxItem>}
    </ListBox>
  )
}

collections/table-sorting-demo.raw:
'use client'

import { useAsyncList } from '@react-stately/data'
import { IconLoader } from 'justd-icons'
import { Card, Table, TableBody, TableCell, TableColumn, TableHeader, TableRow } from 'ui'

interface Character {
  title: string
  director: number
  producer: number
  release_date: number
}

export default function TableSortingDemo() {
  const list = useAsyncList<Character>({
    async load({ signal }) {
      const res = await fetch(`https://swapi.py4e.com/api/films`, {
        signal
      })
      const json = await res.json()
      return {
        items: json.results
      }
    },
    async sort({ items, sortDescriptor }) {
      return {
        items: items.sort((a, b) => {
          // @ts-ignore
          const first = a[sortDescriptor.column]
          // @ts-ignore
          const second = b[sortDescriptor.column]
          let cmp = (parseInt(first) || first) < (parseInt(second) || second) ? -1 : 1
          if (sortDescriptor.direction === 'descending') {
            cmp *= -1
          }
          return cmp
        })
      }
    }
  })
  return (
    <Card>
      <Table
        aria-label="Movies"
        selectionMode="multiple"
        sortDescriptor={list.sortDescriptor}
        onSortChange={list.sort}
      >
        <TableHeader>
          <TableColumn id="title" isRowHeader allowsSorting>
            Title
          </TableColumn>
          <TableColumn id="director" allowsSorting>
            Director
          </TableColumn>
          <TableColumn id="producer" allowsSorting>
            Producer
          </TableColumn>
          <TableColumn id="release_date" allowsSorting>
            Release Date
          </TableColumn>
        </TableHeader>
        <TableBody
          items={list.items}
          renderEmptyState={() => (
            <div className="grid place-content-center p-10">
              <IconLoader className="animate-spin" />
            </div>
          )}
        >
          {(item) => (
            <TableRow id={item.title}>
              <TableCell>{item.title}</TableCell>
              <TableCell>{item.director}</TableCell>
              <TableCell>{item.producer}</TableCell>
              <TableCell>{item.release_date}</TableCell>
            </TableRow>
          )}
        </TableBody>
      </Table>
    </Card>
  )
}

