  return (
    <ToolbarContext.Provider value={{ orientation }}>
      <ToolbarPrimitive
        orientation={orientation}
        {...props}
        className={composeRenderProps(props.className, (className, renderProps) =>
          toolbarStyles({ ...renderProps, className })
        )}
      />
    </ToolbarContext.Provider>
