accordion.raw:
'use client'

import * as React from 'react'

import type { MotionProps } from 'framer-motion'
import { AnimatePresence, motion } from 'framer-motion'
import { IconChevronDown } from 'justd-icons'
import type { ButtonProps } from 'react-aria-components'
import { Button, composeRenderProps } from 'react-aria-components'
import { twMerge } from 'tailwind-merge'
import { tv } from 'tailwind-variants'

import { cn } from './primitive'

interface AccordionContextType extends React.HtmlHTMLAttributes<HTMLDivElement> {
  hideBorder?: boolean
  hideIndicator?: boolean
  disabledKeys?: number[]
  defaultExpandedKeys?: number[] | string[]
}

const AccordionContext = React.createContext<AccordionContextType>({})
const useAccordion = () => React.useContext(AccordionContext)

interface AccordionProps extends AccordionContextType {
  children: React.ReactNode
}

const Accordion = ({
  children,
  disabledKeys,
  hideIndicator,
  hideBorder,
  defaultExpandedKeys,
  ...props
}: AccordionProps) => {
  return (
    <AccordionContext.Provider
      value={{ hideIndicator, defaultExpandedKeys, hideBorder, disabledKeys }}
    >
      <div {...props}>{children}</div>
    </AccordionContext.Provider>
  )
}

const accordionItemStyles = tv({
  base: 'zwx3ai flex group pb-3 relative w-full flex-col border-b'
})

interface AccordionItemContextProps {
  setExpanded?: (index: null | number | string) => void
  isOpen?: boolean
  currentId: number | string
}

const AccordionItemContext = React.createContext<AccordionItemContextProps | undefined>(undefined)
const useAccordionItem = () => {
  const context = React.useContext(AccordionItemContext)
  if (!context) {
    throw new Error('AccordionItem must be used within an Accordion')
  }
  return context
}

interface AccordionItemProps extends React.HTMLAttributes<HTMLDivElement> {
  currentId: number | string
}

const AccordionItem = ({ className, children, currentId }: AccordionItemProps) => {
  const { defaultExpandedKeys, disabledKeys } = useAccordion()
  const [expanded, setExpanded] = React.useState<any>(
    // @ts-ignore - TS doesn't know that defaultExpandedKeys is an array of numbers
    defaultExpandedKeys?.includes(currentId) ? currentId : false
  )
  const isOpen = currentId === expanded
  const isLocked = disabledKeys?.includes(currentId as number)
  return (
    <AccordionItemContext.Provider value={{ setExpanded, isOpen, currentId }}>
      <div
        data-slot="ai-31kxlae0321lsd"
        data-locked={isLocked ?? undefined}
        data-open={isOpen ?? undefined}
        className={accordionItemStyles({ className })}
      >
        {children}
      </div>
    </AccordionItemContext.Provider>
  )
}

interface AccordionContentProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode
}

const AccordionContent = ({ className, children }: AccordionContentProps) => {
  const { isOpen } = useAccordionItem()
  return (
    <AnimatePresence initial={false}>
      {isOpen && (
        <motion.section
          className={cn('overflow-hidden pr-6 dk32xd', className)}
          initial="collapsed"
          animate="open"
          exit="collapsed"
          variants={{
            open: { opacity: 1, height: 'initial' },
            collapsed: { opacity: 0, height: 0 }
          }}
          transition={{ duration: 0.4, ease: [0.04, 0.62, 0.23, 0.98] }}
        >
          <div className="pb-0 pt-1">{children}</div>
        </motion.section>
      )}
    </AnimatePresence>
  )
}

interface AccordionTriggerProps
  extends Omit<ButtonProps & React.RefAttributes<HTMLButtonElement> & MotionProps, 'ref'> {
  children: React.ReactNode
}

const accordionTriggerStyles = tv({
  base: [
    'flex flex-1 rounded-lg text-muted-fg hover:text-fg [&>[data-slot=icon]]:size-6 items-center gap-x-2 pt-3 font-medium'
  ],
  variants: {
    isFocused: {
      true: 'outline-none text-fg'
    },
    isOpen: {
      true: 'text-fg'
    },
    isDisabled: {
      true: 'opacity-50 cursor-default'
    }
  }
})

const AccordionTrigger = ({ className, children, ...props }: AccordionTriggerProps) => {
  const { setExpanded, isOpen, currentId } = useAccordionItem()
  const { hideIndicator, disabledKeys } = useAccordion()
  const isLocked = disabledKeys?.includes(currentId as number)

  const handlePress = () => {
    if (setExpanded) {
      setExpanded(isOpen ? null : currentId)
    }
  }

  const onKeyDownHandler = (e: React.KeyboardEvent<HTMLButtonElement>) => {
    if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {
      e.preventDefault()
      const buttons = document.querySelectorAll('div[data-slot="ai-31kxlae0321lsd"] > button')
      const currentButton = e.currentTarget
      const currentIndex = Array.from(buttons).indexOf(currentButton)
      const totalItems = buttons.length
      let nextIndex = currentIndex + (e.key === 'ArrowDown' ? 1 : -1)

      if (nextIndex >= totalItems) {
        nextIndex = 0
      } else if (nextIndex < 0) {
        nextIndex = totalItems - 1
      }

      ;(buttons[nextIndex] as HTMLElement).focus()
    }
  }

  return (
    <Button
      {...props}
      isDisabled={isLocked}
      onKeyDown={onKeyDownHandler}
      onPress={handlePress}
      className={composeRenderProps(className, (className, renderProps) =>
        accordionTriggerStyles({
          ...renderProps,
          isOpen,
          className
        })
      )}
    >
      {children}
      {!hideIndicator && (
        <IconChevronDown
          className={twMerge(
            'ml-auto transition duration-300 group-disabled:rotate-0',
            isOpen ? 'rotate-180' : 'rotate-0'
          )}
        />
      )}
    </Button>
  )
}

export type { AccordionTriggerProps }
export { Accordion, AccordionItem, AccordionContent, AccordionTrigger }

drawer.raw:
'use client'

import React from 'react'

import {
  animate,
  AnimatePresence,
  type Inertia,
  motion,
  useMotionTemplate,
  useMotionValue,
  useTransform
} from 'framer-motion'
import {
  Button,
  type ButtonProps,
  Dialog,
  type DialogProps,
  Heading,
  type HeadingProps,
  Modal,
  ModalOverlay
} from 'react-aria-components'

import { DialogClose, DialogDescription } from './dialog'
import { cn } from './primitive'

const inertiaTransition: Inertia = {
  type: 'inertia',
  bounceStiffness: 300,
  bounceDamping: 40,
  timeConstant: 300
}
const staticTransition = {
  duration: 0.5,
  ease: [0.32, 0.72, 0, 1]
}
const drawerMargin = 40
const drawerRadius = 32

interface DrawerContextType {
  isOpen: boolean
  openDrawer: () => void
  closeDrawer: () => void
  withNotch?: boolean
}

const DrawerContext = React.createContext<DrawerContextType | undefined>(undefined)

const useDrawerContext = () => {
  const context = React.useContext(DrawerContext)
  if (context === undefined) {
    throw new Error('useDrawerContext must be used within a Drawer')
  }
  return context
}

/**
 * Primitives stick to the drawer, ain't getting exported to other components.
 */
const ModalPrimitive = motion(Modal)
const ModalOverlayPrimitive = motion(ModalOverlay)
const DrawerOverlayPrimitive = (props: React.ComponentProps<typeof ModalOverlayPrimitive>) => {
  const { closeDrawer, withNotch } = useDrawerContext()

  const h = window.innerHeight - drawerMargin
  const y = useMotionValue(h)
  const bgOpacity = useTransform(y, [0, h], [0.5, 0])
  const bg = useMotionTemplate`rgba(0, 0, 0, ${bgOpacity})`

  return (
    <>
      <ModalOverlayPrimitive
        isOpen
        onOpenChange={closeDrawer}
        className="fixed inset-0 z-50"
        style={{ backgroundColor: bg as any }}
      >
        <ModalPrimitive
          className={cn(
            'absolute bottom-0 w-full rounded-t-2xl bg-tertiary shadow-lg ring-1 ring-fg/10',
            props.className
          )}
          initial={{ y: h }}
          animate={{ y: 0 }}
          exit={{ y: h }}
          transition={staticTransition}
          style={{
            y,
            top: drawerMargin,
            paddingBottom: window.screen.height
          }}
          drag="y"
          dragConstraints={{ top: 0 }}
          onDragEnd={(_e, { offset, velocity }) => {
            if (offset.y > window.innerHeight * 0.75 || velocity.y > 10) {
              closeDrawer()
            } else {
              animate(y, 0, { ...inertiaTransition, min: 0, max: 0 })
            }
          }}
          {...props}
        >
          <>
            {withNotch && <div className="notch mx-auto mt-2 h-1.5 w-10 rounded-full bg-fg/20" />}
            {props.children}
          </>
        </ModalPrimitive>
      </ModalOverlayPrimitive>
    </>
  )
}

interface DrawerContentPrimitiveProps extends Omit<React.ComponentProps<typeof Modal>, 'children'> {
  children?: DialogProps['children']
}

const DrawerContentPrimitive = (props: DrawerContentPrimitiveProps) => {
  const { isOpen } = useDrawerContext()

  const h = window.innerHeight - drawerMargin
  const y = useMotionValue(h)

  const bodyScale = useTransform(
    y,
    [0, h],
    [(window.innerWidth - drawerMargin) / window.innerWidth, 1]
  )
  const bodyTranslate = useTransform(y, [0, h], [drawerMargin - drawerRadius, 0])
  const bodyBorderRadius = useTransform(y, [0, h], [drawerRadius, 0])
  return (
    <motion.div
      style={{
        scale: bodyScale,
        borderRadius: bodyBorderRadius,
        y: bodyTranslate,
        transformOrigin: 'center 0'
      }}
    >
      <AnimatePresence>{isOpen && <>{props.children}</>}</AnimatePresence>
    </motion.div>
  )
}

/**
 * Here are the components that get passed around to other components.
 */
const DrawerTrigger = (props: ButtonProps) => {
  const { openDrawer } = useDrawerContext()

  return <Button onPress={openDrawer} {...props} />
}

interface DrawerProps {
  children: React.ReactNode
  isOpen?: boolean
  withNotch?: boolean
  onOpenChange?: (isOpen: boolean) => void
}

const Drawer = ({
  children,
  withNotch = true,
  isOpen: controlledIsOpen,
  onOpenChange
}: DrawerProps) => {
  const [internalIsOpen, setInternalIsOpen] = React.useState(false)

  const isControlled = controlledIsOpen !== undefined
  const isOpen = isControlled ? controlledIsOpen : internalIsOpen

  React.useEffect(() => {
    if (isControlled && onOpenChange) {
      onOpenChange(isOpen)
    }
  }, [isOpen, isControlled, onOpenChange])

  const openDrawer = () => {
    if (isControlled && onOpenChange) {
      onOpenChange(true)
    } else {
      setInternalIsOpen(true)
    }
  }

  const closeDrawer = () => {
    if (isControlled && onOpenChange) {
      onOpenChange(false)
    } else {
      setInternalIsOpen(false)
    }
  }

  if (typeof window === 'undefined') {
    return null
  }

  return (
    <DrawerContext.Provider value={{ isOpen, openDrawer, closeDrawer, withNotch }}>
      {children}
    </DrawerContext.Provider>
  )
}

const DrawerContent = ({
  children,
  className,
  ...props
}: React.ComponentProps<typeof DrawerContentPrimitive>) => {
  return (
    <DrawerContentPrimitive>
      <DrawerOverlayPrimitive {...props}>
        <Dialog className="mx-auto flex h-[calc(var(--visual-viewport-height)-4.5rem)] max-w-3xl flex-col justify-between overflow-y-auto px-4 pt-4 outline-none">
          {(values) => <>{typeof children === 'function' ? children(values) : children}</>}
        </Dialog>
      </DrawerOverlayPrimitive>
    </DrawerContentPrimitive>
  )
}

const DrawerHeader = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div className={cn('flex flex-col gap-y-1 text-center sm:text-left', className)} {...props} />
)

const DrawerTitle = ({ className, ...props }: HeadingProps) => (
  <Heading
    slot="title"
    className={cn('text-lg font-semibold leading-none tracking-tight', className)}
    {...props}
  />
)

const DrawerDescription = DialogDescription

const DrawerBody = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div className={cn('flex-1 overflow-y-auto overflow-x-hidden py-4', className)} {...props} />
)

const DrawerFooter = ({ className, ...props }: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      'flex shrink-0 pb-1 flex-col-reverse gap-2 sm:flex-row sm:justify-between [&_button:first-child:nth-last-child(1)]:w-full',
      className
    )}
    {...props}
  />
)

const DrawerClose = (props: React.ComponentProps<typeof DialogClose>) => {
  return <DialogClose shape="circle" {...props} />
}

export {
  Drawer,
  DrawerBody,
  DrawerClose,
  DrawerContent,
  DrawerDescription,
  DrawerFooter,
  DrawerHeader,
  DrawerTitle,
  DrawerTrigger
}

