const hexToRgb = (hex: string): { r: number; g: number; b: number } | null => {
  const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i
  hex = hex.replace(shorthandRegex, (m, r, g, b) => r + r + g + g + b + b)

  const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex)
  return result
    ? {
        r: parseInt(result[1], 16),
        g: parseInt(result[2], 16),
        b: parseInt(result[3], 16)
      }
    : null
}

const hsbToRgb = (h: number, s: number, b: number): { r: number; g: number; b: number } => {
  s /= 100
  b /= 100
  const k = (n: number) => (n + h / 60) % 6
  const f = (n: number) => b * (1 - s * Math.max(0, Math.min(k(n), 4 - k(n), 1)))
  return {
    r: Math.round(255 * f(5)),
    g: Math.round(255 * f(3)),
    b: Math.round(255 * f(1))
  }
}

const luminance = (r: number, g: number, b: number): number => {
  const a = [r, g, b].map((v) => {
    v /= 255
    return v <= 0.03928 ? v / 12.92 : Math.pow((v + 0.055) / 1.055, 2.4)
  })
  return a[0] * 0.2126 + a[1] * 0.7152 + a[2] * 0.0722
}

const isBrightColor = (color: any): boolean => {
  let r, g, b

  if (typeof color === 'string') {
    if (color.startsWith('#')) {
      const rgb = hexToRgb(color)
      if (rgb) {
        r = rgb.r
        g = rgb.g
        b = rgb.b
      } else {
        return false
      }
    } else if (color.startsWith('rgb')) {
      const rgbValues = color.match(/\d+/g)
      if (rgbValues) {
        r = parseInt(rgbValues[0], 10)
        g = parseInt(rgbValues[1], 10)
        b = parseInt(rgbValues[2], 10)
      } else {
        return false
      }
    } else {
      const namedColors: Record<string, string> = {
        white: '#ffffff',
        black: '#000000'
      }
      const hex = namedColors[color.toLowerCase()]
      if (hex) {
        const rgb = hexToRgb(hex)
        if (rgb) {
          r = rgb.r
          g = rgb.g
          b = rgb.b
        } else {
          return false
        }
      } else {
        return false
      }
    }
  } else if (
    typeof color === 'object' &&
    'hue' in color &&
    'saturation' in color &&
    'brightness' in color
  ) {
    const rgb = hsbToRgb(color.hue, color.saturation, color.brightness)
    r = rgb.r
    g = rgb.g
    b = rgb.b
  } else {
    return false
  }

  const lum = luminance(r, g, b)
  return lum > 0.75
}

const defaultColor = parseColor('hsl(216, 98%, 52%)')
interface ColorSliderProps extends ColorSliderPrimitiveProps {
  label?: string
  description?: string
  showOutput?: boolean
}

const ColorSlider = ({
  className,
  showOutput = true,
  label,
  description,
  ...props
}: ColorSliderProps) => {
  return (
    <ColorSliderPrimitive
      className={cn('flex disabled:opacity-50 w-full flex-col gap-1', className)}
      {...props}
    >
      <div className="flex items-center gap-2">
        {label && <Label className="text-sm [grid-area:label]">{label}</Label>}
        {showOutput && <SliderOutput className="text-sm ml-auto [grid-area:output]" />}
      </div>
      <SliderTrack className="cstrk rounded-md orientation-horizontal:h-8">
        <ColorThumb className="csth top-1/2" />
      </SliderTrack>
      {description && <Description>{description}</Description>}
    </ColorSliderPrimitive>
  )
}

const ColorWheel = ColorWheelPrimitive
const ColorWheelTrack = ColorWheelTrackPrimitive
const ColorSwatchPicker = ColorSwatchPickerPrimitive

export {
  ColorArea,
  ColorSlider,
  ColorSwatch,
  ColorSwatchPicker,
  ColorSwatchPickerItem,
  ColorThumb,
  ColorWheel,
  ColorWheelTrack,
  defaultColor,
  isBrightColor
}

multiple-select.raw:
'use client'

import * as React from 'react'

import { IconChevronLgDown } from 'justd-icons'
import { useFilter } from 'react-aria'
import type { ComboBoxProps as ComboBoxPrimitiveProps, Key } from 'react-aria-components'
import { ComboBox, Group } from 'react-aria-components'
import type { ListData } from 'react-stately'
import { useListData } from 'react-stately'
import { tv } from 'tailwind-variants'

import { Button } from './button'
import type { FieldProps } from './field'
import { Description, Input, Label } from './field'
import { ListBoxItem, ListBoxPicker } from './list-box'
import { PopoverPicker } from './popover'
import { cn } from './primitive'
import type { RestrictedIntent, TagGroupProps } from './tag-group'
import { TagGroup, TagList } from './tag-group'
import { VisuallyHidden } from './visually-hidden'

const multiSelectStyles = tv({
  slots: {
    multiSelectField: 'group flex w-full min-w-80 flex-col gap-1',
    multiSelect: [
      'relative px-1 flex min-h-10 flex-row flex-wrap items-center rounded-lg shadow-sm border',
      'has-[input[data-focused=true]]:border-primary',
      'has-[input[data-invalid=true][data-focused=true]]:border-danger has-[input[data-invalid=true]]:border-danger has-[input[data-invalid=true]]:ring-danger/20',
      'has-[input[data-focused=true]]:ring-4 has-[input[data-focused=true]]:ring-primary/20'
    ],
    chevronButton:
      'size-8 -mr-2 grid place-content-center rounded-sm hover:text-fg focus:text-fg text-muted-fg',
    input: 'flex-1 py-1 px-0.5 ml-1 shadow-none ring-0',
    comboBoxChild: 'inline-flex flex-1 flex-wrap items-center px-0',
    comboBox: 'group peer flex flex-1'
  }
})

const { multiSelectField, multiSelect, chevronButton, input, comboBox, comboBoxChild } =
  multiSelectStyles()

interface FieldState {
  selectedKey: Key | null
  inputValue: string
}

interface SelectedKey {
  id: Key
  name: string
}

interface MultipleSelectProps<T extends object>
  extends FieldProps,
    Omit<
      ComboBoxPrimitiveProps<T>,
      | 'children'
      | 'validate'
      | 'allowsEmptyCollection'
      | 'selectedKey'
      | 'inputValue'
      | 'className'
      | 'value'
      | 'onSelectionChange'
      | 'onInputChange'
    >,
    Pick<TagGroupProps, 'shape'> {
  intent: RestrictedIntent
  items: Array<T>
  selectedItems: ListData<T>
  className?: string
  onItemInserted?: (key: Key) => void
  onItemCleared?: (key: Key) => void
  renderEmptyState?: (inputValue: string) => React.ReactNode
  tag: (item: T) => React.ReactNode
  children: React.ReactNode | ((item: T) => React.ReactNode)
  max?: number
  min?: number
}

const MultipleSelect = <T extends SelectedKey>({
  children,
  items,
  selectedItems,
  onItemCleared,
  onItemInserted,
  className,
  name,
  renderEmptyState,
  ...props
}: MultipleSelectProps<T>) => {
  const [isInvalid, setIsInvalid] = React.useState(false)
  const tagGroupIdentifier = React.useId()
  const triggerRef = React.useRef<HTMLDivElement | null>(null)
  const [width, setWidth] = React.useState(0)

  const { contains } = useFilter({ sensitivity: 'base' })
  const selectedKeys = selectedItems.items.map((i) => i.id)

  const existingTagCount = selectedItems.items.length
  const maxTags = props.max !== undefined ? props.max : Infinity
  const maxTagsToAdd = maxTags - existingTagCount

  const updateInvalidState = (selectedCount: number) => {
    const maxTags = props.max !== undefined ? props.max : Infinity
    setIsInvalid(selectedCount >= maxTags)
  }

  const filter = React.useCallback(
    (item: T, filterText: string) =>
      !selectedKeys.includes(item.id) && contains(item.name, filterText),
    [contains, selectedKeys]
  )

  const accessibleList = useListData({
    initialItems: items,
    filter
  })

  const [fieldState, setFieldState] = React.useState<FieldState>({
    selectedKey: null,
    inputValue: ''
  })

  const onRemove = React.useCallback(
    (keys: Set<Key>) => {
      if (props.min !== undefined && selectedItems.items.length <= props.min) return

      const key = keys.values().next().value
      selectedItems.remove(key)
      setFieldState({ inputValue: '', selectedKey: null })
      onItemCleared?.(key)
      updateInvalidState(selectedItems.items.length - 1)
    },
    [selectedItems, onItemCleared, props.min]
  )

  const onSelectionChange = (id: Key | null) => {
    if (!id) return

    const item = accessibleList.getItem(id)
    const maxTags = props.max !== undefined ? props.max : Infinity

    if (!item) return

    if (!selectedKeys.includes(id)) {
      if (selectedItems.items.length >= maxTags) {
        setIsInvalid(true)

        const timeoutId = setTimeout(() => {
          setIsInvalid(false)
        }, 2000)

        return () => clearTimeout(timeoutId)
      }

      selectedItems.append(item)
      setFieldState({
        inputValue: '',
        selectedKey: id
      })
      onItemInserted?.(id)
    }

    accessibleList.setFilterText('')
  }
  const onInputChange = (v: string) => {
    setFieldState((prevState) => ({
      inputValue: v,
      selectedKey: v === '' ? null : prevState.selectedKey
    }))

    accessibleList.setFilterText(v)
  }

  const popLast = React.useCallback(() => {
    if (
      selectedItems.items.length == 0 ||
      (props.min !== undefined && selectedItems.items.length <= props.min)
    ) {
      return
    }

    const endKey = selectedItems.items[selectedItems.items.length - 1]

    if (endKey !== null) {
      selectedItems.remove(endKey.id)
      onItemCleared?.(endKey.id)
      updateInvalidState(selectedItems.items.length - 1)
    }

    setFieldState({
      inputValue: '',
      selectedKey: null
    })
  }, [selectedItems, onItemCleared])

  const onKeyDownCapture = React.useCallback(
    (e: React.KeyboardEvent<HTMLInputElement>) => {
      if (e.key === 'Backspace' && fieldState.inputValue === '') {
        popLast()
      }
    },
    [popLast, fieldState.inputValue]
  )

  React.useEffect(() => {
    const trigger = triggerRef.current
    if (!trigger) return

    const observer = new ResizeObserver((entries) => {
      for (const entry of entries) {
        setWidth(entry.target.clientWidth)
      }
    })

    observer.observe(trigger)
    return () => {
      observer.unobserve(trigger)
    }
  }, [triggerRef])

  const triggerButtonRef = React.useRef<HTMLButtonElement | null>(null)

  return (
    <div className={multiSelectField({ className })}>
      {props.label && <Label>{props.label}</Label>}
      <Group className={props.isDisabled ? 'opacity-50' : ''}>
        <div ref={triggerRef} className={multiSelect({ className })}>
          <TagGroup
            shape={props.shape}
            intent={props.intent}
            aria-label="Selected items"
            id={tagGroupIdentifier}
            onRemove={onRemove}
          >
            <TagList
              items={selectedItems.items}
              className={cn(
                selectedItems.items.length !== 0 && 'px-1 py-1.5',
                'last:[&_.jdt3lr2x]:-mr-1 outline-none gap-1.5',
                props.shape === 'square' && '[&_.jdt3lr2x]:rounded-[calc(var(--radius)-2.5px)]'
              )}
            >
              {props.tag}
            </TagList>
          </TagGroup>
          <ComboBox
            {...props}
            isInvalid={isInvalid}
            aria-label="Available items"
            allowsEmptyCollection
            className={comboBox()}
            items={accessibleList.items}
            selectedKey={fieldState.selectedKey}
            inputValue={fieldState.inputValue}
            onSelectionChange={onSelectionChange}
            onInputChange={onInputChange}
          >
            <div className={comboBoxChild({ className })}>
              <Input
                placeholder={maxTagsToAdd <= 0 ? 'Remove one to add more' : props.placeholder}
                className={input()}
                onBlur={() => {
                  setFieldState({
                    inputValue: '',
                    selectedKey: null
                  })
                  accessibleList.setFilterText('')
                }}
                onKeyDownCapture={onKeyDownCapture}
              />

              <VisuallyHidden>
                <Button
                  slot="remove"
                  aria-label="Remove"
                  appearance="plain"
                  size="square-petite"
                  ref={triggerButtonRef}
                >
                  <IconChevronLgDown />
                </Button>
              </VisuallyHidden>
            </div>
            <PopoverPicker
              className="max-w-none"
              style={{ width: `${width}px` }}
              triggerRef={triggerRef}
              trigger="ComboBox"
            >
              <ListBoxPicker
                renderEmptyState={() =>
                  renderEmptyState ? (
                    renderEmptyState(fieldState.inputValue)
                  ) : (
                    <Description className="p-3 block">
                      {fieldState.inputValue ? (
                        <>
                          No results found for:{' '}
                          <strong className="font-medium text-fg">{fieldState.inputValue}</strong>
                        </>
                      ) : (
                        `No options`
                      )}
                    </Description>
                  )
                }
                selectionMode="multiple"
              >
                {children}
              </ListBoxPicker>
            </PopoverPicker>
          </ComboBox>
          <div className="relative px-1 ml-auto flex items-center justify-center" aria-hidden>
            <button
              type="button"
              className={chevronButton()}
              onClick={() => triggerButtonRef.current?.click()}
              tabIndex={-1}
            >
              <IconChevronLgDown className="peer/[data-open]:rotate-180 size-4" />
            </button>
          </div>
        </div>
      </Group>
      {props.description && <Description>{props.description}</Description>}
      {name && <input hidden name={name} value={selectedKeys.join(',')} readOnly />}
    </div>
  )
}

const MultipleSelectItem = ListBoxItem

export { MultipleSelect, MultipleSelectItem, type SelectedKey }

