  defaultVariants: {
    shape: 'circle',
    size: 'medium'
  }
})

type Status = 'away' | 'online' | 'offline' | 'dnd' | 'idle'

interface AvatarProps
  extends React.ComponentPropsWithoutRef<'span'>,
    VariantProps<typeof avatarStyles> {
  src?: string | null
  initials?: string
  alt?: string
  status?: Status
  className?: string
  role?: string
}

const Avatar = ({
  status,
  src = null,
  initials,
  alt = '',
  children,
  className,
  shape,
  size,
  role = 'avatar',
  ...props
}: AvatarProps) => {
  const badgeId = React.useId()
