      away: 'bg-red-500',
      online: 'bg-green-500',
      offline: 'bg-gray-500',
      dnd: 'bg-yellow-500',
      idle: 'bg-gray-500'
    }
  },
  defaultVariants: {
    size: 'medium',
    status: 'idle'
