    <MenuContext.Provider value={{ respectScreen }}>
      <MenuTriggerPrimitive {...props}>
        <>{props.children}</>
      </MenuTriggerPrimitive>
    </MenuContext.Provider>
  )
}

const SubMenu = ({ delay = 0, ...props }) => (
  <SubmenuTriggerPrimitive {...props} delay={delay}>
    {props.children}
