}: ModalContentProps) => {
  const _isDismissable = role === 'alertdialog' ? false : isDismissable
  return (
    <ModalOverlayPrimitive
      isDismissable={_isDismissable}
      className={composeRenderProps(classNames?.overlay, (className, renderProps) => {
        return modalOverlayStyles({
          ...renderProps,
          isBlurred,
          className
        })
      })}
      {...props}
    >
      <ModalPrimitive
        className={composeRenderProps(classNames?.content, (className, renderProps) =>
          modalContentStyles({
            ...renderProps,
            size,
            className
          })
        )}
        {...props}
      >
        <Dialog role={role}>
          {({ close }) => (
            <>
              {children}
              {closeButton && <DialogCloseIndicator close={close} isDismissable={_isDismissable} />}
            </>
          )}
        </Dialog>
      </ModalPrimitive>
    </ModalOverlayPrimitive>
  )
}

export {
  modalContentStyles,
  Modal,
  ModalBody,
  ModalClose,
  ModalContent,
  ModalDescription,
  ModalFooter,
  ModalHeader,
  ModalTitle,
  ModalTrigger,
  type ModalContentProps,
  modalOverlayStyles
}

